import React, { useContext } from "react";
import "./Card.css";
import { StoreContext } from "../../../Context/StoreContext";
import { assets } from "../../../assets/assets";


const Card = () => {
  const { cartItems, food_list, removeFromCard } = useContext(StoreContext);
  return (
    <div className="card">
      <div className="card-items">
        <div className="card-items-title">
          <p>Items</p>
          <p>Title</p>
          <p>Price</p>
          <p>Quantity</p>
          <p>Total price</p>
          <p>Remove</p>
        </div>
        <br />
        <hr />
      {food_list.map((item,index)=>{
       if(cartItems[item._id]>0) 
       {
        return(
       <div>
           <div className="card-items-title card-items-item">
            <img src={item.image} alt="" />
            <p>{item.name}</p>
            <p>${item.price}</p>
            <p>{cartItems[item._id]}</p>
            <p>${item.price*cartItems[item._id]}</p>
            <p onClick={()=>removeFromCard(item._id)} className="cross"><img src={assets.cross_icon} alt="" /></p>
          </div>
          <hr />
       </div>
        )
       }
      })}
      </div>
      .cart-
    </div>
  );
};

export default Card;
