import React, { useContext } from "react";
import "./FoodItem.css";
import { assets } from "../../assets/assets";
import { StoreContext } from "../../Context/StoreContext";

const FoodItem = ({ id, name, price, description, image }) => {
  const { cartItems, addToCard, removeFromCard } = useContext(StoreContext);

  return (
    <div className="food-item">
      <div className="food-item-img-container">
        {/* Food Image */}
        <img
          className="food-item-image"
          src={image || assets.default_food_image} // Fallback image if `image` is undefined
          alt={name || "Food item"}
        />

        {/* Add or Counter */}
        {!cartItems[id] ? (
          <img
            className="add"
            onClick={() => addToCard(id)}
            src={assets.add_icon_white}
            alt="Add to cart"
          />
        ) : (
          <div className="food-item-counter">
            <img
              onClick={() => removeFromCard(id)}
              src={assets.remove_icon_red}
              alt="Remove one from cart"
            />
            <p>{cartItems[id]}</p>
            <img
              onClick={() => addToCard(id)}
              src={assets.add_icon_green}
              alt="Add one more"
            />
          </div>
        )}
      </div>

      {/* Food Info */}
      <div className="food-item-info">
        <div className="food-item-name-rating">
          <p>{name || "Unnamed Dish"}</p>
          <img
            src={assets.rating_starts}
            alt="Rating stars"
          />
        </div>
        <p className="food-item-desc">{description || "No description available"}</p>
        <p className="food-item-price">${price?.toFixed(2) || "0.00"}</p>
      </div>
    </div>
  );
};

export default FoodItem;








