// 



import React, { useState } from "react";

const CardComponent = () => {
    const [clickedCards, setClickedCards] = useState({}); // Store clicked state per card

    const cards = [
        { id: 1, title: "Card 1" },
        { id: 2, title: "Card 2" },
        { id: 3, title: "Card 3" },
    ];

    const handleCardClick = (id) => {
        setClickedCards((prev) => ({
            ...prev,
            [id]: !prev[id], // Toggle clicked state for specific card
        }));
    };

    return (
        <div style={{ display: "flex", gap: "10px" }}>
            {cards.map((card) => (
                <div
                    key={card.id}
                    onClick={() => handleCardClick(card.id)}
                    style={{
                        border: "1px solid black",
                        padding: "20px",
                        cursor: "pointer",
                        backgroundColor: clickedCards[card.id] ? "lightgreen" : "white",
                    }}
                >
                    {card.title}
                </div>
            ))}
        </div>
    );
};

export default CardComponent;
