import React, { useContext } from "react";
import "./Card.css";
import { StoreContext } from "../../../Context/StoreContext";

const Card = () => {
  const { cardItems, food_list, removeItem } = useContext(StoreContext); // Assuming removeItem function exists

  return (
    <div className="cart">
      <div className="card-items">
        <div className="card-items-title">
          <p>Items</p>
          <p>Title</p>
          <p>Price</p>
          <p>Quantity</p>
          <p>Total</p>
          <p>Remove</p>
        </div>
        <hr />
        {food_list.map((item) => {
          if (cardItems[item._id] > 0) {
            return (
              <div key={item._id} className="card-items-item">
                <img src={item.image} alt={item.name} />
                <p>{item.name}</p>
                <p>{item.price}</p>
                <p>{cardItems[item._id]}</p>
                <p>{item.price * cardItems[item._id]}</p>
                <button onClick={() => removeItem(item._id)}>Remove</button>
              </div>
            );
          }
          return null; // Explicitly return null for items with no quantity in the cart
        })}
      </div>
    </div>
  );
};

export default Card;
