import React, { useContext } from "react";
import "./Card.css";
import { StoreContext } from "../../../Context/StoreContext";

const Card = () => {
  const { cartItems, food_list, removeFromCard } = useContext(StoreContext);

  return (
    <div className="card">
      <div className="card-items">
        <div className="card-items-title">
          <p>Items</p>
          <p>Title</p>
          <p>Price</p>
          <p>Quantity</p>
          <p>Total</p>
          <p>Remove</p>
        </div>
        <br />
        <hr />

        {food_list.map((item) => {
          if (cartItems[item._id] > 0) {
            const quantity = cartItems[item._id];
            const total = item.price * quantity;

            return (
              <div className="card-items-item" key={item._id}>
                <p>{item.name}</p>
                <p>{item.price}</p>
                <p>{quantity}</p>
                <p>{total}</p>
                <button onClick={() => removeFromCard(item._id)}>Remove</button>
              </div>
            );
          }
          return null; // Avoid rendering anything if quantity is 0
        })}
      </div>
    </div>
  );
};

export default Card;




