import React, { useContext } from "react";
import "./FoodItem.css";
import { assets } from "../../assets/assets";
import { StoreContext } from "../../Context/StoreContext";

const FoodItem = ({
  id,
  name = "Unnamed Dish",
  price = 0,
  description = "No description available",
  image,
}) => {
  const { cartItems, addToCard, removeFromCard } = useContext(StoreContext);

  // Format price
  const formattedPrice = new Intl.NumberFormat("en-US", {
    style: "currency",
    currency: "USD",
  }).format(price);

  return (
    <div className="food-item">
      {/* Image Section */}
      <div className="food-item-img-container">
        <img
          className="food-item-image"
          src={image || assets.default_food_image}
          alt={name}
        />
        {!cartItems[id] ? (
          <img
            className="add"
            onClick={() => addToCard(id)}
            src={assets.add_icon_white}
            alt="Add to cart"
          />
        ) : (
          <div className="food-item-counter">
            <img
              onClick={() => removeFromCard(id)}
              src={assets.remove_icon_red}
              alt="Remove one item from cart"
            />
            <p>{cartItems[id]}</p>
            <img
              onClick={() => addToCard(id)}
              src={assets.add_icon_green}
              alt="Add one more item"
            />
          </div>
        )}
      </div>

      {/* Info Section */}
      <div className="food-item-info">
        <div className="food-item-name-rating">
          <p>{name}</p>
          <img src={assets.rating_starts} alt="Rating stars" />
        </div>
        <p className="food-item-desc">{description}</p>
        <p className="food-item-price">{formattedPrice}</p>
      </div>
    </div>
  );
};

export default FoodItem;
